body.script:
maptilersdk.config.apiKey = 'Ac1UVUxWl0WYiAANdAIc';
const map = new maptilersdk.Map({
  container: 'map', // container's id or the HTML element to render the map
  style: maptilersdk.MapStyle.STREETS,
  center: [-118.39290536282017, 33.94348484504489], // starting position [lng, lat]
  zoom: 15, // starting zoom
});

map.on('load', function() {
  map.addSource("aerial-source", {
    "type": "image",
    "url": "https://cors-anywhere.herokuapp.com/http://34.211.17.90/maps/lax/level-3/Lax-Level3.svg",
    "coordinates": [
      [-118.42187322018101, 33.9488251580048], // top-left corner [lng, lat]
      [-118.39290536282017, 33.9488251580048], // top-right corner [lng, lat]
      [-118.39290536282017, 33.93814453208498], // bottom-right corner [lng, lat]
      [-118.42187322018101, 33.93814453208498]  // bottom-left corner [lng, lat]
    ]
  });

  map.addLayer({
    "id": "overlay",
    "source": "aerial-source",
    "type": "raster",
    "paint": {
      "raster-opacity": 0.85
    }
  });
});
